mapper.select:
select g.gid, g.name, gt.name gtype, g.producer,g.description, gs.name gspecification_name,g.price, g.photo from goods g left join goods_type gt on g.gtype_id = gt.gtype_id left join goods_specification gs on g.gspecification_id = gs.gspecification_id
where g.name like #{name}
limit #{offset}, #{pagesize}

select count(gid)
from goods

	where name like #{name}

mapper.insert:
insert into goods( name, gtype_id, producer, gspecification_id, price,
description, photo)
values(#{name}, #{gtype_id}, #{producer},
#{gspecification_id}, #{price}, #{description}
,#{photo});

mapper.update:
update goods set name =#{name}, gtype_id=#{gtype_id},
producer=#{producer},
description=#{description},gspecification_id=#{gspecification_id},
price=#{price}


	,photo=#{photo}

where gid = #{gid}
