mapper.select:
select soid, g.name gname, gs.name gsname,s1.name supplierName,s2.name storageName, sales_order_gspecification_id,so.appendix appendix,out_of_store_time outOfStoreTime, sales_order_store_id,sales_order_supplier_id, num, g.price, (num * g.price) sum, so.name name, os.name auditStatusName,so.audit_status auditStatus, createdtime,c.cid cid, c.name clientName, creater_id createrId, audited_id auditedId, audited_time auditedTime from sales_order so left outer join goods g on so.sales_order_gid = g.gid left outer join goods_specification gs on so.sales_order_gspecification_id = gs.gspecification_id left outer join supplier s1 on so.sales_order_supplier_id = s1.sid left outer join storage s2 on so.sales_order_store_id = s2.sid left outer join employee e on so.creater_id = e.eid left outer join client c on so.sales_order_client_id = c.cid left outer join order_status os on so.audit_status = os.osid
true
and soid = #{soid}
and gid = #{gid}
and g.name like CONCAT(CONCAT('%',#{gname}),'%')
and s1.sid = #{supplierId}
and e.name like CONCAT(CONCAT('%',#{createrName}),'%')
and s2.sid = #{storageId}
and so.audit_status = #{auditStatus}
and c.name like CONCAT(CONCAT('%',#{clientName}),'%')
and so.createdtime like CONCAT(CONCAT('%',date_format(#{createdtime},'%Y-%m-%d')),'%')
and so.out_of_store_time like CONCAT(CONCAT('%',date_format(#{outOfStoreTime},'%Y-%m-%d')),'%')
limit #{offset}, #{pagesize}

select count(*) from sales_order so left outer join goods g on so.sales_order_gid = g.gid left outer join goods_specification gs on so.sales_order_gspecification_id = gs.gspecification_id left outer join supplier s1 on so.sales_order_supplier_id = s1.sid left outer join storage s2 on so.sales_order_store_id = s2.sid left outer join employee e on so.creater_id = e.eid left outer join client c on so.sales_order_client_id = c.cid left outer join order_status os on so.audit_status = os.osid
true
and soid = #{soid}
and g.gid = #{gid}
and g.name like #{gname}
and s1.sid = #{supplierId}
and s2.sid = #{storageId}
and so.createrId = #{createrId}
and so.audit_status = #{auditStatus}
and so.createdtime like CONCAT(CONCAT('%',date_format(#{createdtime},'%Y-%m-%d')),'%')
and c.name like CONCAT(CONCAT('%',#{clientName}),'%')
and e.name like #{createrName}
and so.out_of_store_time like CONCAT(CONCAT('%',date_format(#{outOfStoreTime},'%Y-%m-%d')),'%')
